#!/usr/bin/env python 
# -*- coding: utf-8 -*-

depts = {}
courses = {}
currentDept = None
currentCourse = None
currentDiv = None

def setDept (dept):
    global currentDept, currentDiv, currentCourse, depts
    currentDept = dept.upper()
    depts[currentDept] = { "courses": [] }
    currentDiv = None
    currentCourse = None

def setDiv (div):
    global currentDiv
    currentDiv = div

def setCourse (course):
    global currentCourse, currentDept, currentDiv, courses, depts
    course = currentDept + " " + course
    currentCourse = course
    courses[currentCourse] = { 
        "department": currentDept,
        "division": currentDiv,
    }
    depts[currentDept]["courses"].append(course)

def setTitle (title):
    global currentCourse, courses
    courses[currentCourse]["title"] = title

def setDescrip (descr):
    global currentCourse, courses
    courses[currentCourse]["description"] = descr

def setEnrollRestrict (text):
    global currentCourse, courses
    courses[currentCourse]["enroll-restrict"] = text

def setInstruct (instructor):
    global currentCourse, courses
    courses[currentCourse]["instructor"] = instructor

def setTerms (text):
    global currentCourse, courses
    courses[currentCourse]["terms"] = text

def setGe (text):
    global currentCourse, courses
    courses[currentCourse]["ge"] = text

def setPrereqs (text):
    global currentCourse, courses
    courses[currentCourse]["prereqs"] = text

def setConcReq (text):
    global currentCourse, courses
    courses[currentCourse]["concurrent_req"] = text

