setEnrollRestrict("sophomore and junior anthropology majors")
setInstruct("M. Fernando")
setCourse("151")
setTitle("Workshop in Ethnography")
setTerms("S")
setDescrip("Through demonstration, practice, and participation, acquire skills in collecting and analyzing cultural data. Work with members of other cultures and with each other to learn to identify significant cultural patterns. Lectures and readings provide added perspective and a theoretical base.")
setPrereqs("course 2")
setInstruct("A. Kramer")
setCourse("152")
setTitle("Survey of Cultural Anthropological Theory")
setTerms("W")
setDescrip("Major figures, ideas, and writings in 19th- and 20th-century cultural anthropology surveyed. Students cannot receive credit for this course and course 252.")
setPrereqs("course 2 and satisfaction of the Entry Level Writing and Composition requirements; enrollment restricted to anthropology majors")
setInstruct("A. Tsing")
setCourse("153")
setTitle("Medicine and Colonialism")
setTerms("*")
setDescrip("Addresses the overlapping relationship between medicine and colonialism in the 19th century, with attention to post-colonial theory and contemporary studies of post-colonial medical pluralism in the 20th century.")
setPrereqs("courses 2 and 134")
setInstruct("The Staff")
setCourse("154")
