setPrereqs("course 3")
setInstruct("J. Daehnke")
setCourse("176F")
setTitle("California Archaeology")
setTerms("W")
setDescrip("Introduces the Native peoples of California from an archaeological perspective. Covering the past 13,000 years, a variety of geographic and temporal settings are examined as well as current research in California archaeology.")
setPrereqs("course 3")
setInstruct("T. Schneider")
